		gs.addErrorMessage('Equipment costs for ' + evtname + ' exceed the event budget.');
	}
	//Populate cost & remaining budget
	current.event_cost = cost;
	current.remaining_budget = budget - cost;
	current.update();
	action.setRedirectURL(current);
